import React, { Component } from 'react';
import Particles from 'react-particles-js';
import Navigation from '../components/Navigation';
import Logo from '../components/Logo';
import ImageLinkForm from '../components/ImageLinkForm';
import FaceReco from '../components/FaceReco';
import Rank from '../components/Rank';
import Clarifai from 'clarifai';
import './App.css';

const app = new Clarifai.App({
 apiKey: '2c8dcc1c39c242d284f70ebbb9584cdb'
});

const particlesOptions = {
  particles: {
    number: {
      value: 30,
      density: {
        enable: true,
        value_area: 800
      }
    }
  }
}

class App extends Component {
	constructor(){
		super()
		this.state= {
			input : '',
			imgUrl : '',
			box : {}
		}
	}

	calFaceLocation = (data) => {
		const face = data.outputs[0].data.regions[0].region_info.bounding_box;
		const image = document.getElementById('inputImg');
		const width = Number(image.width);
		const height = Number(image.height);
	}

	onInputChange = (event) => {
		console.log(event.target.value);
		this.setState({input : event.target.value});
		return event.target.value;
	}

	onButtonSubmit = () => {
		this.setState({imgUrl : this.state.input});
		app.models.predict(
			Clarifai.FACE_DETECT_MODEL, this.state.input)
			.then(response => this.calFaceLocation(response)).catch(err => console.log('error reaching clarifai'));
	}

	render(){
	  return (
		    <div className="App">
		    	<Particles className='particles'
          		params={particlesOptions}
        		/>
		    	<Navigation />
		      	<Logo />
		      	<Rank />
		      	<ImageLinkForm onInputChange={this.onInputChange} onButtonSubmit={this.onButtonSubmit}/>
		   		<FaceReco imgUrl={this.state.imgUrl}/>
		    </div>
	  );
	}
}

export default App;
